sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var attachment = new GlideSysAttachment();
var attached = attachment.copy('sc_req_item', inputs.RITMsysID, 'sys_data_source', inputs.DatasourcesysID );

	if(attached){
			gs.executeNow(inputs.Scheduleimportrecord);	
		}
})(inputs, outputs);

